#include <wolfssl/wolfcrypt/settings.h>
#include "common/util.h"

int benchmark_test(void *args);

void wolfssl_thread_entry(void *pvParameters) {
    FSP_PARAMETER_NOT_USED(pvParameters);
    initialise_monitor_handles();
    benchmark_test(0);
    while (1);
}
