    t = (t / dv) << 22;
    r += t;
    d -= (sp_int128)t * div;
    t = (sp_digit)(d >> 13);
    t = t / (dv << 9);
    r += t;
    d -= (sp_int128)t * div;
    t = (sp_digit)d;
    t = t / div;
    r += t;
    d -= (sp_int128)t * div;
